mapper.select:
select ha.nextApprovalId
from holiday_approval ha
         left join sys_dict_data s on s.dict_type = 'holiday_type' and ha.holiday_type_id = s.dict_code
where ha.holiday_type_id = #{holidayTypeId}
  and ha.role_id = #{roleId}
  and ha.current_approved_index = #{currentApprovedIndex}
  and ha.del_flag = 0
limit 1

select ha.holiday_approval_id, ha.holiday_type_id, s.dict_label, s.dict_value, ha.role_id, (select r.role_name from sys_role r where ha.role_id = r.role_id) as role_name, ha.approved_role_id, (select r.role_name from sys_role r where ha.approved_role_id = r.role_id) as approved_role_name, ha.current_approved_index, ha.next_approval_id, ha.del_flag from holiday_approval ha left join sys_dict_data s on s.dict_type = 'holiday_type' and ha.holiday_type_id = s.dict_code where ha.holiday_type_id = #{holidayTypeId} and ha.role_id = #{roleId}
and ha.current_approved_index = #{currentApprovedIndex}
and ha.del_flag = 0 order by current_approved_index

mapper.delete:
update holiday_approval
set del_flag = 1
where holiday_approval_id = #{holidayApprovalId}
